#!/usr/bin/env python3
# -*- coding:utf-8 -*-

import struct, base64


#检查任意文件是否是位图文件，如果是，打印出图片大小和颜色数


bmp_data = base64.b64decode('')

def bmp_info(data):
    s = struct.unpack('<ccIIIIIIHH', data[:30])
    if s[0] == b'B' and (s[1] == b'W' or b'A'):
        return {
            'width': s[6],
            'height': s[7],
            'color': s[9]
        }

# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')